INSERT INTO place_details (
    INSERT INTO sections (title, content, image_id, place_detail_id) VALUES
        ('Lịch sử và Văn hóa', 'Ao Bà Om là một di tích lịch sử văn hóa quan trọng của người Khmer tại Trà Vinh, gắn liền với truyền thuyết về Bà Om, người đã khai phá vùng đất này. Khu vực này thường tổ chức các lễ hội truyền thống vào dịp Tết Chôl Chnăm Thmây.', NULL, 1),
        ('Hoạt động Tham quan', 'Du khách có thể chèo thuyền quanh hồ, tham quan chùa Khmer gần đó, hoặc thưởng thức các món ăn đặc sản Trà Vinh như bánh tét lá cẩm.', NULL, 1),
        ('Mẹo Tham quan', 'Nên đến Ao Bà Om vào buổi sáng sớm để tận hưởng không khí mát mẻ và chụp ảnh cảnh bình minh tuyệt đẹp bên hồ.', NULL, 1),
        ('Đa dạng Sinh học', 'Hồ Ba Bể là nhà của nhiều loài động thực vật quý hiếm, bao gồm voọc mũi hếch và các loài cá nước ngọt đặc trưng, nằm trong Vườn Quốc gia Ba Bể.', NULL, 2),
        ('Trải nghiệm Thuyền bè', 'Một chuyến thuyền trên hồ Ba Bể sẽ đưa du khách qua động Puông và các bản làng người Tày, mang đến trải nghiệm văn hóa độc đáo.', NULL, 2),
        ('Cầu Vàng - Điểm nhấn Kiến trúc', 'Cầu Vàng tại Bà Nà Hills là công trình kiến trúc nổi tiếng với thiết kế hai bàn tay khổng lồ nâng đỡ cây cầu, thu hút hàng triệu du khách chụp ảnh.', NULL, 3),
        ('Ẩm thực tại Bà Nà', 'Khu du lịch Bà Nà Hills có nhiều nhà hàng phục vụ các món ăn từ Á đến Âu, đặc biệt là buffet với đa dạng món đặc sản miền Trung.', NULL, 3),
        ('Hoạt động Giải trí', 'Bãi biển An Bàng là nơi lý tưởng để tắm biển, lướt sóng, hoặc tham gia các lớp yoga bên bờ biển vào sáng sớm.', NULL, 4),
        ('Ẩm thực Biển', 'Gần bãi biển An Bàng có nhiều quán hải sản tươi sống, nơi du khách có thể thưởng thức tôm hùm nướng hoặc mực nướng mọi.', NULL, 4),
        ('Đặc trưng Cát Đen', 'Bãi biển Cồn Đen nổi bật với lớp cát đen độc đáo, hình thành từ hoạt động địa chất, tạo nên khung cảnh khác biệt so với các bãi biển cát trắng.', NULL, 5),
        ('Khám phá Làng chài', 'Du khách có thể đi bộ khám phá các làng chài gần bãi biển Cồn Đen hoặc tham gia tour câu cá cùng ngư dân địa phương.', NULL, 5),
        ('Giải trí Biển', 'Bãi biển Cửa Lò cung cấp các hoạt động như lướt ván, dù lượn, hoặc thuê xe đạp đôi dạo quanh bờ biển.', NULL, 6),
        ('Lịch sử Địa phương', 'Bãi biển Cửa Lò từng là nơi nghỉ dưỡng của vua Bảo Đại, với nhiều câu chuyện lịch sử thú vị về vùng đất Nghệ An.', NULL, 6),
        ('Cảnh Hoàng hôn', 'Bãi biển Cửa Tùng được mệnh danh là "nữ hoàng hoàng hôn" với khung cảnh mặt trời lặn tuyệt đẹp, lý tưởng để chụp ảnh.', NULL, 7),
        ('Ẩm thực Hải sản', 'Du khách có thể thưởng thức các món hải sản tươi ngon như ghẹ hấp, cá mú nướng tại các quán ven bãi biển Cửa Tùng.', NULL, 7),
        ('Không gian Tĩnh lặng', 'Bãi biển Hồ Bể - Vĩnh Châu là nơi lý tưởng để tránh xa sự ồn ào của thành phố, với không gian yên bình và ít du khách.', NULL, 8),
        ('Khám phá Thiên nhiên', 'Du khách có thể khám phá các khu vực rừng ngập mặn gần bãi biển Hồ Bể hoặc đi bộ dọc bờ biển để tìm hiểu hệ sinh thái.', NULL, 8),
        ('Bãi biển Đẹp Nhất Thế giới', 'Bãi biển Mỹ Khê từng được Forbes bình chọn là một trong những bãi biển đẹp nhất hành tinh, với cát trắng mịn và nước biển trong xanh.', NULL, 9),
        ('Dịch vụ Du lịch', 'Mỹ Khê có nhiều khu nghỉ dưỡng cao cấp, nhà hàng ven biển, và các hoạt động như dù lượn, jet ski, phù hợp cho mọi lứa tuổi.', NULL, 9),
        ('Văn hóa Làng chài', 'Bãi biển Quy Nhơn gần các làng chài, nơi du khách có thể trải nghiệm cuộc sống ngư dân và thưởng thức hải sản tươi ngon.', NULL, 10),
        ('Hoạt động Thư giãn', 'Du khách có thể thuê thuyền kayak để khám phá các vịnh nhỏ gần bãi biển Quy Nhơn hoặc tham gia tour lặn ngắm san hô.', NULL, 10),
        ('Không gian Hoang sơ', 'Bãi biển Tân Thành là nơi lý tưởng cho những ai yêu thích sự tĩnh lặng, với bãi cát rộng và không khí trong lành.', NULL, 11),
        ('Ẩm thực Địa phương', 'Gần bãi biển Tân Thành có các quán ăn phục vụ món ăn miền Tây như bánh xèo và cá lóc nướng trui.', NULL, 11),
        ('Cảnh quan Thơ mộng', 'Bãi biển Thiên Cầm có hình dạng giống cây đàn, với sóng vỗ nhẹ nhàng và không gian lý tưởng để thư giãn.', NULL, 12),
        ('Hoạt động Biển', 'Du khách có thể tham gia câu cá hoặc tắm biển tại Thiên Cầm, nơi có làn nước trong xanh và bãi cát mịn.', NULL, 12),
        ('Đá Bào mòn Độc đáo', 'Bãi Đá Nhảy Lý Hòa nổi bật với những khối đá lớn bị sóng biển bào mòn, tạo hình thù kỳ thú, thu hút du khách yêu thiên nhiên.', NULL, 13),
        ('Khám phá Thiên nhiên', 'Du khách có thể đi bộ dọc bãi biển để chiêm ngưỡng các khối đá hoặc chụp ảnh với cảnh quan độc đáo.', NULL, 13),
        ('Hoạt động Giải trí', 'Bãi Sau Vũng Tàu là nơi lý tưởng cho các hoạt động như tắm biển, chơi bóng chuyền bãi biển, hoặc thuê phao bơi.', NULL, 14),
        ('Ẩm thực Ven biển', 'Bãi Sau có nhiều quán hải sản phục vụ các món như cua rang me, sò điệp nướng phô mai, thu hút du khách.', NULL, 14),
        ('Văn hóa Dân tộc Thái', 'Bản Áng là nơi sinh sống của người Thái, với các hoạt động văn hóa như múa sạp và thưởng thức món ăn truyền thống.', NULL, 15),
        ('Khám phá Thiên nhiên', 'Du khách có thể đi bộ qua rừng thông Bản Áng hoặc thuê xe đạp để khám phá cảnh quan đồi chè xanh mướt.', NULL, 15),
        ('Nhà sàn Người Tày', 'Bản Pác Ngòi nổi bật với những ngôi nhà sàn truyền thống của người Tày, mang đến trải nghiệm văn hóa độc đáo.', NULL, 16),
        ('Trải nghiệm Làng bản', 'Du khách có thể ở homestay tại Bản Pác Ngòi, tham gia các hoạt động như dệt thổ cẩm hoặc nấu ăn cùng người dân địa phương.', NULL, 16),
        ('Lịch sử Cà phê', 'Bảo tàng Thế giới Cà phê trưng bày lịch sử ngành cà phê Việt Nam, từ thời kỳ trồng trọt đến văn hóa thưởng thức cà phê.', NULL, 17),
        ('Trải nghiệm Thưởng thức', 'Du khách có thể tham gia các buổi nếm thử cà phê tại bảo tàng hoặc mua các sản phẩm cà phê Trung Nguyên chất lượng cao.', NULL, 17),
        ('Văn hóa Khmer', 'Bảo Tàng Văn Hóa Dân Tộc Khmer lưu giữ các hiện vật quý giá, tái hiện đời sống và phong tục của người Khmer ở Nam Bộ.', NULL, 18),
        ('Hoạt động Tham quan', 'Du khách có thể tham quan các gian trưng bày và tìm hiểu về trang phục, nhạc cụ truyền thống của người Khmer.', NULL, 18),
        ('Cảnh quan Thiên nhiên', 'Bàu Cạn là hồ nước ngọt yên bình, với các đảo nhỏ và rừng cây bao quanh, lý tưởng cho các chuyến picnic.', NULL, 19),
        ('Hoạt động Chèo thuyền', 'Du khách có thể thuê thuyền để khám phá hồ Bàu Cạn hoặc chụp ảnh với cảnh quan thiên nhiên tuyệt đẹp.', NULL, 19),
        ('Đồi cát Trắng', 'Bàu Trắng nổi tiếng với đồi cát trắng mịn và hồ nước xanh biếc, tạo nên khung cảnh giống sa mạc giữa lòng Việt Nam.', NULL, 20),
        ('Hoạt động Mạo hiểm', 'Du khách có thể thuê xe địa hình để trượt cát hoặc chụp ảnh với cảnh quan độc đáo tại Bàu Trắng.', NULL, 20),
        ('Lịch sử Tàu Không Số', 'Bến K15 là nơi ghi dấu những chuyến tàu không số trong kháng chiến chống Mỹ, mang ý nghĩa lịch sử sâu sắc.', NULL, 21),
        ('Tham quan Di tích', 'Du khách có thể tham quan các hiện vật và nghe kể về những chiến công của các chiến sĩ tại Bến K15.', NULL, 21),
        ('Chèo thuyền Nho Quế', 'Bến thuyền hẻm Tu Sản là điểm xuất phát cho các chuyến chèo thuyền trên sông Nho Quế, với cảnh quan hùng vĩ của Hà Giang.', NULL, 22),
        ('Khám phá Thiên nhiên', 'Du khách có thể chụp ảnh với vách đá dựng đứng hoặc tham gia trekking quanh khu vực hẻm Tu Sản.', NULL, 22),
        ('Bãi biển Hoang sơ', 'Biển Đông A là nơi lý tưởng cho những ai yêu thích khám phá thiên nhiên, với bãi cát rộng và ít dấu chân người.', NULL, 23),
        ('Hoạt động Cắm trại', 'Du khách có thể cắm trại qua đêm trên bãi biển Đông A để tận hưởng không khí yên bình và ngắm sao trời.', NULL, 23),
        ('Trải nghiệm Du thuyền', 'Biển Ngọc Cruise mang đến hành trình khám phá vịnh Hạ Long trên du thuyền sang trọng, với các tiện ích cao cấp.', NULL, 24),
        ('Ẩm thực Trên thuyền', 'Du khách có thể thưởng thức các món hải sản tươi ngon và ẩm thực cao cấp ngay trên du thuyền Biển Ngọc.', NULL, 24),
        ('Quy trình Cá kho', 'Làng Vũ Đại nổi tiếng với món cá kho truyền thống, được nấu trong nồi đất với công thức gia truyền hàng trăm năm.', NULL, 25),
        ('Trải nghiệm Ẩm thực', 'Du khách có thể tham gia các lớp học nấu cá kho hoặc mua sản phẩm cá kho làm quà tại làng Vũ Đại.', NULL, 25),
        ('Văn hóa Sông nước', 'Chợ nổi Cái Răng là biểu tượng của văn hóa sông nước miền Tây, nơi giao thương sôi động vào mỗi buổi sáng sớm.', NULL, 26),
        ('Trải nghiệm Thuyền bè', 'Du khách có thể thuê thuyền để khám phá chợ nổi, mua trái cây tươi hoặc thưởng thức bún nước lèo trên thuyền.', NULL, 26),
        ('Mùa lúa chín', 'Cánh đồng Mường Than đẹp nhất vào mùa lúa chín, với màu vàng rực rỡ trải dài khắp thung lũng.', NULL, 27),
        ('Khám phá Nông nghiệp', 'Du khách có thể tham gia trải nghiệm gặt lúa hoặc chụp ảnh với cảnh quan đồng lúa tuyệt đẹp.', NULL, 27),
        ('Ý nghĩa Lịch sử', 'Cánh đồng Mường Thanh gắn liền với chiến thắng Điện Biên Phủ, mang ý nghĩa lịch sử và văn hóa sâu sắc.', NULL, 28),
        ('Cảnh quan Thiên nhiên', 'Du khách có thể thuê xe đạp để khám phá cánh đồng lúa Mường Thanh hoặc tham quan các di tích gần đó.', NULL, 28),
        ('Vườn Quốc gia Cát Bà', 'Cát Bà là nơi có Vườn Quốc gia Cát Bà, với hệ sinh thái đa dạng và nhiều loài động thực vật quý hiếm.', NULL, 29),
        ('Hoạt động Sinh thái', 'Du khách có thể tham gia trekking trong rừng, chèo kayak quanh vịnh hoặc lặn ngắm san hô tại Cát Bà.', NULL, 29),
        ('Kiến trúc Dây văng', 'Cầu Cần Thơ là biểu tượng của miền Tây, với thiết kế dây văng hiện đại và tầm nhìn đẹp vào ban đêm.', NULL, 30),
        ('Ngắm cảnh Đêm', 'Du khách có thể đi bộ trên cầu Cần Thơ vào buổi tối để ngắm ánh đèn lung linh và sông Hậu.', NULL, 30),
        ('Lịch sử Cầu', 'Cầu Hoàng Văn Thụ là một trong những cây cầu lâu đời ở Hải Phòng, chứng kiến nhiều sự kiện lịch sử quan trọng.', NULL, 31),
        ('Khám phá Thành phố', 'Từ cầu Hoàng Văn Thụ, du khách có thể dễ dàng di chuyển đến các điểm tham quan khác ở Hải Phòng như Nhà hát Lớn.', NULL, 31),
        ('Lịch sử Cầu Long Biên', 'Cầu Long Biên là chứng nhân lịch sử của Hà Nội, từng bị phá hủy trong chiến tranh nhưng vẫn giữ nét kiến trúc độc đáo.', NULL, 32),
        ('Chụp ảnh Cầu', 'Cầu Long Biên là điểm lý tưởng để chụp ảnh, đặc biệt vào buổi sáng sớm hoặc hoàng hôn với khung cảnh sông Hồng.', NULL, 32),
        ('Ý nghĩa Kinh tế', 'Cầu Mỹ Thuận kết nối hai tỉnh Tiền Giang và Vĩnh Long, góp phần thúc đẩy giao thương miền Tây Nam Bộ.', NULL, 33),
        ('Ngắm cảnh Sông Tiền', 'Du khách có thể dừng chân trên cầu để ngắm cảnh sông Tiền và các hoạt động sông nước nhộn nhịp.', NULL, 33),
        ('Kiến trúc Độc đáo', 'Cầu Rồng Đà Nẵng nổi bật với thiết kế hình rồng uốn lượn, đặc biệt phun lửa và nước vào cuối tuần.', NULL, 34),
        ('Sự kiện Cuối tuần', 'Du khách nên đến cầu Rồng vào tối thứ Bảy hoặc Chủ Nhật để xem màn trình diễn phun lửa và nước độc đáo.', NULL, 34),
        ('Không gian Lãng mạn', 'Cầu Tình Yêu là nơi các cặp đôi gắn khóa tình yêu, với khung cảnh sông Hàn thơ mộng vào ban đêm.', NULL, 35),
        ('Chụp ảnh Kỷ niệm', 'Du khách có thể chụp ảnh với các ổ khóa tình yêu hoặc đi dạo trên cầu để tận hưởng không khí lãng mạn.', NULL, 35),
        ('Ẩm thực Đêm', 'Chợ đêm Đà Lạt là nơi thưởng thức các món ăn đường phố như bánh tráng nướng, sữa đậu nành nóng và khoai lang nướng.', NULL, 36),
        ('Mua sắm Thủ công', 'Chợ đêm Đà Lạt có nhiều gian hàng bán đồ thủ công, áo len, và quà lưu niệm độc đáo của vùng cao nguyên.', NULL, 36),
        ('Món ăn Đường phố', 'Chợ đêm Tân An nổi tiếng với các món ăn miền Tây như bánh bò, chè bưởi, và cá lóc nướng trui.', NULL, 37),
        ('Không khí Nhộn nhịp', 'Chợ đêm Tân An thu hút du khách với không gian sôi động và các hoạt động giao lưu văn hóa địa phương.', NULL, 37),
        ('Chợ Truyền thống', 'Chợ Đông Kinh là nơi giao thương các sản phẩm nông sản, hải sản, và hàng hóa đặc trưng của Hải Phòng.', NULL, 38),
        ('Ẩm thực Hải sản', 'Du khách có thể thưởng thức các món hải sản tươi ngon như cua bể, tôm sú tại các quầy hàng trong chợ Đông Kinh.', NULL, 38),
        ('Hải sản Tươi sống', 'Chợ Đông Ngô Quyền là điểm đến lý tưởng để mua hải sản tươi hoặc thưởng thức các món như hàu nướng phô mai.', NULL, 39),
        ('Văn hóa Địa phương', 'Chợ Đông Ngô Quyền mang đậm nét văn hóa Hải Phòng, với các hoạt động buôn bán nhộn nhịp và thân thiện.', NULL, 39),
        ('Chợ Vải Lớn Nhất', 'Chợ Ninh Hiệp là trung tâm vải lớn nhất miền Bắc, với đa dạng các loại vải từ lụa, cotton đến vải may công nghiệp.', NULL, 40),
        ('Mua sắm Thời trang', 'Du khách có thể tìm mua vải để may áo dài, vest, hoặc các sản phẩm may mặc thủ công tại chợ Ninh Hiệp.', NULL, 40),
        ('Văn hóa Sông nước', 'Chợ nổi Ngã Bảy là nơi giao thương nhộn nhịp, với các thuyền chở trái cây, rau củ và đặc sản miền Tây.', NULL, 41),
        ('Trải nghiệm Thuyền', 'Du khách có thể thuê thuyền để khám phá chợ nổi Ngã Bảy và thưởng thức các món ăn trên sông.', NULL, 41),
        ('Giao thương Sông nước', 'Chợ nổi Ngã Năm là biểu tượng của văn hóa sông nước Sóc Trăng, với các hoạt động buôn bán sôi nổi vào sáng sớm.', NULL, 42),
        ('Ẩm thực Miền Tây', 'Du khách có thể thưởng thức bún riêu cua hoặc bánh cống ngay trên thuyền tại chợ nổi Ngã Năm.', NULL, 42),
        ('Văn hóa Dân tộc', 'Chợ phiên Bắc Hà là nơi giao lưu của các dân tộc H’Mông, Dao, với các sản phẩm thủ công như thổ cẩm và đồ trang sức.', NULL, 43),
        ('Ẩm thực Độc đáo', 'Chợ phiên Bắc Hà có các món ăn đặc trưng như thắng cố, phở chua, và rượu ngô Bản Phố.', NULL, 43),
