INSERT INTO place_details (
    id, place_id, description, time_open, time_close, is_closed, is_open_all_day, best_time_to_visit, price_range_top, price_range_bottom, alternative_name, operator, url
) VALUES
(1, 1, 'Ao Bà Om, một hồ nước thiêng liêng nằm giữa lòng Trà Vinh, được bao quanh bởi những cây cổ thụ hàng trăm năm tuổi, tạo nên không gian yên bình và huyền bí. Nơi đây không chỉ là điểm đến tâm linh mà còn là nơi lý tưởng để thư giãn, tận hưởng không khí trong lành và khám phá văn hóa Khmer đặc sắc qua các lễ hội như Chôl Chnăm Thmây hay Ok Om Bok.', '06:00:00', '18:00:00', 0, 0, 'Thời điểm lý tưởng để ghé thăm Ao Bà Om là vào sáng sớm hoặc chiều muộn, khi tiết trời mát mẻ và ánh nắng dịu nhẹ làm nổi bật vẻ đẹp thơ mộng của hồ. Đặc biệt, nếu đến vào mùa lễ hội (tháng 4 hoặc tháng 10 âm lịch), bạn sẽ được hòa mình vào không khí sôi động và văn hóa đặc sắc của người Khmer.', 50000, 0, 'Hồ Bà Om', 'Ban Quản lý Di tích Trà Vinh', 'https://travinh.gov.vn/ao-ba-om'),
(2, 2, 'Hồ Ba Bể, viên ngọc xanh giữa lòng Vườn Quốc gia Ba Bể, là một trong những hồ nước ngọt đẹp nhất Việt Nam. Bao quanh bởi những dãy núi đá vôi hùng vĩ và rừng nguyên sinh, hồ mang đến khung cảnh thiên nhiên hoang sơ, yên bình. Du khách có thể chèo thuyền kayak, khám phá các hang động hoặc tìm hiểu văn hóa của người Tày tại các bản làng gần đó.', '00:00:00', '23:59:59', 0, 1, 'Thời gian tốt nhất để ghé thăm Hồ Ba Bể là từ tháng 9 đến tháng 11, khi thời tiết mát mẻ, không gian trong lành và cảnh sắc hồ nước đạt đến độ hoàn mỹ. Mùa xuân (tháng 2-4) cũng là thời điểm lý tưởng để tham gia các lễ hội truyền thống của người dân tộc Tày.', 200000, 50000, 'Hồ Ba Bể', 'Ban Quản lý Vườn Quốc gia Ba Bể', 'https://babenationalpark.vn'),
(3, 3, 'Bà Nà Hills, “Đà Lạt của miền Trung”, là khu du lịch nổi tiếng với khí hậu mát mẻ quanh năm và khung cảnh tuyệt đẹp trên đỉnh núi. Nơi đây có Cầu Vàng độc đáo, làng Pháp lãng mạn, công viên giải trí Fantasy Park và những khu vườn hoa rực rỡ, mang đến trải nghiệm đa dạng cho mọi du khách.', '07:00:00', '22:00:00', 0, 0, 'Thời gian lý tưởng để ghé thăm Bà Nà Hills là từ tháng 4 đến tháng 8, khi thời tiết khô ráo, dễ dàng di chuyển và ngắm cảnh. Tránh mùa mưa (tháng 10-12) để tận hưởng trọn vẹn vẻ đẹp của khu du lịch.', 900000, 750000, 'Khu du lịch Bà Nà', 'Sun World', 'https://banahills.sunworld.vn'),
(4, 4, 'Bãi biển An Bàng, một trong những bãi biển đẹp nhất Hội An, nổi bật với bờ cát trắng mịn, làn nước trong xanh và không gian yên tĩnh. Đây là điểm đến lý tưởng để thư giãn, tắm biển hoặc thưởng thức hải sản tươi ngon tại các nhà hàng ven biển.', '00:00:00', '23:59:59', 0, 1, 'Thời điểm tốt nhất để đến Bãi biển An Bàng là từ tháng 2 đến tháng 8, khi thời tiết khô ráo và biển êm. Buổi sáng sớm hoặc hoàng hôn là lúc bãi biển đẹp nhất, với ánh nắng dịu nhẹ và không khí dễ chịu.', 200000, 0, 'Biển An Bàng', NULL, 'https://hoian.gov.vn/bai-bien-an-bang'),
(5, 5, 'Bãi biển Cồn Đen, nằm ở Thanh Hóa, là một bãi biển hoang sơ với bờ cát đen độc đáo, ít được biết đến nhưng đầy quyến rũ. Không gian yên bình, ít đông đúc cùng những hàng dừa xanh mát khiến nơi đây trở thành điểm đến lý tưởng cho những ai yêu thích sự tĩnh lặng.', '00:00:00', '23:59:59', 0, 1, 'Thời gian lý tưởng để ghé thăm Bãi biển Cồn Đen là từ tháng 5 đến tháng 8, khi thời tiết ấm áp và biển trong xanh. Buổi sáng sớm là lúc tuyệt vời để ngắm bình minh và tận hưởng không khí trong lành.', 100000, 0, 'Biển Cồn Đen', NULL, NULL),
(6, 6, 'Bãi biển Cửa Lò, một trong những bãi biển nổi tiếng nhất Nghệ An, thu hút du khách bởi bãi cát trắng mịn, làn nước trong xanh và không khí sôi động. Đây là nơi lý tưởng để tắm biển, thưởng thức hải sản tươi ngon và tham gia các hoạt động giải trí trên biển.', '00:00:00', '23:59:59', 0, 1, 'Thời điểm tốt nhất để ghé thăm Bãi biển Cửa Lò là từ tháng 4 đến tháng 8, khi thời tiết nắng ấm và biển êm. Hoàng hôn trên biển là khoảnh khắc không thể bỏ lỡ, mang đến khung cảnh lãng mạn và yên bình.', 150000, 0, 'Biển Cửa Lò', NULL, 'https://nghean.gov.vn/bai-bien-cua-lo'),
(7, 7, 'Bãi biển Cửa Tùng, được mệnh danh là “nữ hoàng của các bãi biển Quảng Trị”, nổi bật với bờ cát trắng mịn và làn nước trong xanh. Không gian yên bình cùng những hàng phi lao rợp bóng tạo nên một bức tranh thiên nhiên thơ mộng, lý tưởng cho những ai muốn tránh xa sự ồn ào.', '00:00:00', '23:59:59', 0, 1, 'Thời gian lý tưởng để đến Bãi biển Cửa Tùng là từ tháng 5 đến tháng 8, khi thời tiết khô ráo và biển êm. Buổi chiều muộn là lúc tuyệt vời để ngắm hoàng hôn và thưởng thức hải sản địa phương.', 100000, 0, 'Biển Cửa Tùng', NULL, NULL),
(8, 8, 'Bãi biển Hồ Bể, một viên ngọc ẩn của Trà Vinh, mang vẻ đẹp hoang sơ với bờ cát mịn và những rặng dừa xanh mát. Nơi đây là điểm đến lý tưởng cho những ai muốn tận hưởng không gian yên tĩnh và khám phá đời sống người dân miền biển.', '00:00:00', '23:59:59', 0, 1, 'Thời điểm tốt nhất để ghé thăm Bãi biển Hồ Bể là từ tháng 4 đến tháng 9, khi thời tiết khô ráo và biển trong xanh. Buổi sáng sớm hoặc hoàng hôn là lúc cảnh sắc bãi biển đẹp nhất.', 100000, 0, 'Biển Hồ Bể', NULL, NULL),
(9, 9, 'Bãi biển Mỹ Khê, một trong những bãi biển đẹp nhất thế giới, nằm tại Đà Nẵng với bãi cát trắng mịn, nước biển trong xanh và không gian sôi động. Đây là nơi lý tưởng để tắm biển, tham gia các hoạt động thể thao nước và thưởng thức ẩm thực biển.', '00:00:00', '23:59:59', 0, 1, 'Thời gian lý tưởng để đến Bãi biển Mỹ Khê là từ tháng 2 đến tháng 8, khi thời tiết nắng đẹp và biển êm. Buổi sáng sớm là thời điểm tuyệt vời để tắm biển và tận hưởng không khí trong lành.', 200000, 0, 'Biển Mỹ Khê', NULL, 'https://danang.gov.vn/bai-bien-my-khe'),
(10, 10, 'Bãi biển Quy Nhơn, nằm tại Bình Định, nổi bật với bờ cát vàng óng ánh, làn nước trong xanh và không gian yên bình. Đây là điểm đến lý tưởng cho những ai muốn thư giãn, tắm biển và khám phá các món ăn đặc sản địa phương như bánh xèo tôm nhảy.', '00:00:00', '23:59:59', 0, 1, 'Thời điểm tốt nhất để ghé thăm Bãi biển Quy Nhơn là từ tháng 3 đến tháng 9, khi thời tiết khô ráo và biển êm. Hoàng hôn trên biển là khoảnh khắc tuyệt đẹp, mang đến cảm giác thư thái và lãng mạn.', 150000, 0, 'Biển Quy Nhơn', NULL, 'https://binhdinh.gov.vn/bai-bien-quy-nhon'),
(11, 11, 'Bãi biển Tân Thành, một bãi biển hoang sơ tại Tiền Giang, thu hút du khách bởi vẻ đẹp mộc mạc, bờ cát trải dài và không gian yên tĩnh. Đây là nơi lý tưởng để thư giãn, tận hưởng không khí biển và khám phá đời sống của người dân địa phương.', '00:00:00', '23:59:59', 0, 1, 'Thời gian lý tưởng để ghé thăm Bãi biển Tân Thành là từ tháng 4 đến tháng 9, khi thời tiết khô ráo và biển trong xanh. Buổi sáng sớm là lúc tuyệt vời để ngắm bình minh và tận hưởng không khí trong lành.', 100000, 0, 'Biển Tân Thành', NULL, NULL),
(12, 12, 'Bãi biển Thiên Cầm, nằm tại Hà Tĩnh, nổi tiếng với bãi cát trắng mịn, làn nước trong xanh và không gian yên bình. Tên gọi “Thiên Cầm” bắt nguồn từ hình dáng bãi biển giống cây đàn trời, mang đến cảm giác thơ mộng và quyến rũ.', '00:00:00', '23:59:59', 0, 1, 'Thời điểm tốt nhất để đến Bãi biển Thiên Cầm là từ tháng 4 đến tháng 8, khi thời tiết nắng ấm và biển êm. Hoàng hôn trên biển là khoảnh khắc tuyệt đẹp, lý tưởng để chụp ảnh và thư giãn.', 150000, 0, 'Biển Thiên Cầm', NULL, 'https://hatinh.gov.vn/bai-bien-thien-cam'),
(13, 13, 'Bãi biển Đá Nhảy, nằm tại Quảng Bình, nổi bật với những khối đá lớn bị sóng biển bào mòn tạo thành nhiều hình thù độc đáo. Không gian hoang sơ, bãi cát trắng mịn và làn nước trong xanh khiến nơi đây trở thành điểm đến lý tưởng cho những ai yêu thích thiên nhiên.', '00:00:00', '23:59:59', 0, 1, 'Thời gian lý tưởng để ghé thăm Bãi biển Đá Nhảy là từ tháng 4 đến tháng 8, khi thời tiết khô ráo và biển êm. Buổi sáng sớm hoặc hoàng hôn là lúc cảnh sắc bãi biển đẹp nhất.', 100000, 0, 'Biển Đá Nhảy', NULL, 'https://quangbinh.gov.vn/bai-bien-da-nhay'),
(14, 14, 'Bãi Sau, một trong những bãi biển nổi tiếng nhất Vũng Tàu, thu hút du khách bởi bãi cát vàng, làn nước trong xanh và không gian sôi động. Đây là nơi lý tưởng để tắm biển, tham gia các hoạt động thể thao nước và thưởng thức hải sản tươi ngon.', '00:00:00', '23:59:59', 0, 1, 'Thời điểm tốt nhất để đến Bãi Sau là từ tháng 3 đến tháng 8, khi thời tiết nắng ấm và biển êm. Hoàng hôn trên biển là khoảnh khắc tuyệt đẹp, mang đến không gian lãng mạn.', 200000, 0, 'Biển Bãi Sau', NULL, 'https://vungtau.gov.vn/bai-sau'),
(15, 15, 'Rừng thông Bản Áng, nằm tại Mộc Châu, là một điểm đến lý tưởng cho những ai yêu thích thiên nhiên. Những hàng thông xanh mướt, hồ nước trong veo và không khí mát mẻ tạo nên một khung cảnh thơ mộng, gợi nhớ đến Đà Lạt.', '06:00:00', '18:00:00', 0, 0, 'Thời gian lý tưởng để ghé thăm Rừng thông Bản Áng là từ tháng 11 đến tháng 2, khi tiết trời se lạnh và không gian rừng thông trở nên lãng mạn hơn bao giờ hết. Mùa hoa cải (tháng 12-1) cũng là thời điểm tuyệt vời để chụp ảnh.', 50000, 20000, 'Bản Áng', 'Ban Quản lý Du lịch Mộc Châu', 'https://mocchau.gov.vn/rung-thong-ban-ang'),
(16, 16, 'Làng Pác Ngòi, nằm bên Hồ Ba Bể, là một bản làng truyền thống của người Tày với những ngôi nhà sàn cổ kính và không gian yên bình. Du khách có thể trải nghiệm văn hóa địa phương, thưởng thức ẩm thực Tày và chèo thuyền khám phá hồ.', '06:00:00', '18:00:00', 0, 0, 'Thời điểm lý tưởng để ghé thăm Làng Pác Ngòi là từ tháng 9 đến tháng 11, khi thời tiết mát mẻ và cảnh sắc hồ Ba Bể đẹp nhất. Mùa xuân (tháng 2-4) cũng là thời điểm diễn ra các lễ hội truyền thống của người Tày.', 100000, 30000, 'Bản Pác Ngòi', NULL, 'https://babenationalpark.vn/lang-pac-ngoi'),
(17, 17, 'Bảo tàng Thế giới Cà phê tại Buôn Ma Thuột là một điểm đến độc đáo, nơi du khách có thể khám phá lịch sử và văn hóa cà phê Việt Nam. Với kiến trúc độc đáo và không gian trưng bày hiện đại, bảo tàng mang đến trải nghiệm thú vị cho mọi lứa tuổi.', '08:00:00', '17:00:00', 0, 0, 'Thời gian lý tưởng để ghé thăm Bảo tàng Thế giới Cà phê là từ tháng 11 đến tháng 3, khi thời tiết mát mẻ và dễ chịu. Buổi sáng là thời điểm tốt nhất để tham quan và thưởng thức cà phê nguyên chất.', 100000, 50000, 'Bảo tàng Cà phê', 'Trung Nguyên Legend', 'https://thegioicaphe.vn'),
(18, 18, 'Bảo tàng Văn hóa Dân tộc Khmer Trà Vinh là nơi lưu giữ những giá trị văn hóa đặc sắc của người Khmer, từ kiến trúc chùa chiền đến các hiện vật truyền thống. Đây là điểm đến lý tưởng để tìm hiểu về lịch sử và đời sống của cộng đồng Khmer tại miền Tây.', '07:30:00', '17:00:00', 0, 0, 'Thời điểm tốt nhất để ghé thăm Bảo tàng Văn hóa Dân tộc Khmer Trà Vinh là từ tháng 10 đến tháng 4, khi thời tiết khô ráo và dễ chịu. Tham gia các lễ hội như Ok Om Bok (tháng 10 âm lịch) để trải nghiệm văn hóa Khmer sôi động.', 30000, 0, 'Bảo tàng Khmer Trà Vinh', 'Ban Quản lý Di tích Trà Vinh', 'https://travinh.gov.vn/bao-tang-khmer'),
(19, 19, 'Bãi biển Bàu Cạn, nằm tại Gia Lai, là một bãi biển hoang sơ với bãi cát trắng và làn nước trong xanh. Không gian yên tĩnh và cảnh sắc thiên nhiên nguyên sơ khiến nơi đây trở thành điểm đến lý tưởng cho những ai yêu thích sự tĩnh lặng.', '00:00:00', '23:59:59', 0, 1, 'Thời gian lý tưởng để ghé thăm Bãi biển Bàu Cạn là từ tháng 3 đến tháng 8, khi thời tiết khô ráo và biển êm. Buổi sáng sớm hoặc hoàng hôn là lúc cảnh sắc bãi biển đẹp nhất.', 100000, 0, 'Biển Bàu Cạn', NULL, NULL),
(20, 20, 'Đồi cát Bàu Trắng, nằm tại Bình Thuận, được ví như “tiểu sa mạc Sahara” của Việt Nam. Những đồi cát trắng mịn trải dài cùng hồ nước xanh mát tạo nên khung cảnh hùng vĩ và độc đáo, thu hút những ai yêu thích khám phá và chụp ảnh.', '06:00:00', '18:00:00', 0, 0, 'Thời điểm lý tưởng để ghé thăm Đồi cát Bàu Trắng là từ tháng 11 đến tháng 4, khi thời tiết mát mẻ và dễ chịu. Buổi sáng sớm hoặc chiều muộn là lúc ánh nắng tạo nên những góc chụp đẹp nhất.', 50000, 20000, 'Đồi cát Trắng', NULL, 'https://binhthuan.gov.vn/doi-cat-bau-trang'),
(21, 21, 'Bến K15 - Đường Hồ Chí Minh trên biển, tại Thanh Hóa, là một di tích lịch sử quan trọng, nơi ghi dấu những chiến công của Đoàn 759 trong kháng chiến chống Mỹ. Đây là điểm đến lý tưởng để tìm hiểu lịch sử và tưởng nhớ những người anh hùng.', '07:00:00', '17:00:00', 0, 0, 'Thời gian tốt nhất để ghé thăm Bến K15 là từ tháng 3 đến tháng 10, khi thời tiết khô ráo và dễ dàng di chuyển. Buổi sáng là thời điểm thích hợp để tham quan và tìm hiểu lịch sử.', 30000, 0, 'Bến K15', 'Ban Quản lý Di tích Thanh Hóa', NULL),
(22, 22, 'Bến thuyền Hẻm Tu Sản, nằm tại Hà Giang, là điểm xuất phát để khám phá hẻm vực Tu Sản - một trong những kỳ quan thiên nhiên hùng vĩ nhất Việt Nam. Chèo thuyền trên sông Nho Quế, du khách sẽ được chiêm ngưỡng những vách đá dựng đứng và dòng sông xanh ngọc bích.', '07:00:00', '17:00:00', 0, 0, 'Thời điểm lý tưởng để ghé thăm Bến thuyền Hẻm Tu Sản là từ tháng 9 đến tháng 11, khi thời tiết mát mẻ và dòng sông Nho Quế đẹp nhất. Buổi sáng sớm là thời điểm tuyệt vời để chèo thuyền và ngắm cảnh.', 150000, 50000, 'Hẻm Tu Sản', NULL, 'https://hagiang.gov.vn/hem-tu-san'),
(23, 23, 'Biển Đông A, một bãi biển hoang sơ tại Cà Mau, mang đến không gian yên bình với bãi cát mịn và làn nước trong xanh. Đây là điểm đến lý tưởng cho những ai muốn tận hưởng thiên nhiên và khám phá đời sống miền biển.', '00:00:00', '23:59:59', 0, 1, 'Thời gian lý tưởng để ghé thăm Biển Đông A là từ tháng 11 đến tháng 4, khi thời tiết khô ráo và biển êm. Buổi sáng sớm hoặc hoàng hôn là lúc cảnh sắc bãi biển đẹp nhất.', 100000, 0, 'Biển Đông A', NULL, NULL),
(24, 24, 'Du thuyền Biển Ngọc, tại Hạ Long, mang đến trải nghiệm sang trọng khi khám phá Vịnh Hạ Long - di sản thế giới được UNESCO công nhận. Du khách sẽ được chiêm ngưỡng những hòn đảo đá vôi, hang động kỳ bí và thưởng thức ẩm thực cao cấp trên tàu.', '08:00:00', '20:00:00', 0, 0, 'Thời điểm lý tưởng để trải nghiệm Du thuyền Biển Ngọc là từ tháng 10 đến tháng 4, khi thời tiết mát mẻ và vịnh Hạ Long trong xanh. Buổi sáng hoặc chiều muộn là thời điểm tuyệt vời để ngắm cảnh và chụp ảnh.', 2000000, 1000000, 'Biển Ngọc Cruise', 'Công ty Du thuyền Biển Ngọc', 'https://bienngoccruise.vn'),
(25, 25, 'Cá Kho Làng Vũ Đại, tại Hà Nam, là một địa điểm nổi tiếng với món cá kho truyền thống trứ danh. Du khách không chỉ được thưởng thức món ăn đậm đà hương vị mà còn có cơ hội tìm hiểu về văn hóa ẩm thực và đời sống làng quê Việt Nam.', '07:00:00', '17:00:00', 0, 0, 'Thời gian tốt nhất để ghé thăm Cá Kho Làng Vũ Đại là từ tháng 11 đến tháng 3, khi thời tiết mát mẻ và món cá kho được chế biến theo cách truyền thống nhất. Buổi trưa là thời điểm lý tưởng để thưởng thức món ăn.', 500000, 200000, 'Làng Vũ Đại', NULL, 'https://cakho.vn'),
(26, 26, 'Chợ Nổi Cái Răng, tại Cần Thơ, là một trong những chợ nổi nổi tiếng nhất miền Tây, nơi du khách có thể trải nghiệm cảnh mua bán nhộn nhịp trên sông và thưởng thức các món ăn đặc sản như bún nước lèo, bánh xèo.', '05:00:00', '12:00:00', 0, 0, 'Thời điểm lý tưởng để ghé thăm Chợ Nổi Cái Răng là từ 5h đến 8h sáng, khi chợ tấp nập nhất và không khí mát mẻ. Tháng 11 đến tháng 4 là mùa khô, phù hợp để khám phá chợ nổi và các hoạt động trên sông.', 100000, 30000, 'Chợ Cái Răng', NULL, 'https://cantho.gov.vn/cho-noi-cai-rang'),
(27, 27, 'Cánh Đồng Mường Than, nằm tại Thanh Hóa, là một trong những cánh đồng lớn nhất Tây Bắc, nổi bật với cảnh sắc thiên nhiên hùng vĩ và không gian yên bình. Đây là điểm đến lý tưởng cho những ai yêu thích khám phá văn hóa dân tộc Thái và thiên nhiên.', '06:00:00', '18:00:00', 0, 0, 'Thời gian lý tưởng để ghé thăm Cánh Đồng Mường Than là từ tháng 9 đến tháng 11, khi thời tiết mát mẻ và cảnh sắc đồng lúa chín vàng đẹp nhất. Buổi sáng sớm hoặc chiều muộn là lúc tuyệt vời để chụp ảnh.', 50000, 0, 'Mường Than', NULL, NULL),
(28, 28, 'Cánh Đồng Mường Thanh, tại Điện Biên, là một thung lũng rộng lớn với cảnh sắc thiên nhiên tuyệt đẹp và ý nghĩa lịch sử sâu sắc. Nơi đây không chỉ thu hút bởi vẻ đẹp đồng lúa mà còn bởi các di tích lịch sử như Đồi A1 và Bảo tàng Điện Biên Phủ.', '06:00:00', '18:00:00', 0, 0, 'Thời điểm lý tưởng để ghé thăm Cánh Đồng Mường Thanh là từ tháng 9 đến tháng 11, khi lúa chín vàng và thời tiết mát mẻ. Mùa hoa ban nở (tháng 2-3) cũng là thời điểm đẹp để khám phá văn hóa Thái.', 50000, 0, 'Mường Thanh', NULL, 'https://dienbien.gov.vn/canh-dong-muong-thanh'),
(29, 29, 'Đảo Cát Bà, thuộc Hải Phòng, là một điểm đến nổi tiếng với hệ sinh thái đa dạng, những bãi biển đẹp và Vườn Quốc gia Cát Bà. Du khách có thể tắm biển, leo núi, khám phá hang động hoặc thưởng thức hải sản tươi ngon.', '00:00:00', '23:59:59', 0, 1, 'Thời gian lý tưởng để ghé thăm Đảo Cát Bà là từ tháng 4 đến tháng 10, khi thời tiết ấm áp và biển êm. Mùa hè (tháng 6-8) là thời điểm lý tưởng để tắm biển và tham gia các hoạt động ngoài trời.', 300000, 100000, 'Cát Bà', 'Ban Quản lý Vườn Quốc gia Cát Bà', 'https://catba.gov.vn'),
(30, 30, 'Cầu Cần Thơ, biểu tượng của miền Tây, là cây cầu dây văng dài nhất Đông Nam Á khi hoàn thành. Với vẻ đẹp hiện đại và tầm nhìn toàn cảnh sông Hậu, đây là điểm đến lý tưởng để ngắm cảnh và chụp ảnh, đặc biệt vào ban đêm khi cầu sáng rực ánh đèn.', '00:00:00', '23:59:59', 0, 1, 'Thời điểm lý tưởng để ghé thăm Cầu Cần Thơ là vào buổi tối, khi ánh đèn chiếu sáng tạo nên khung cảnh lung linh. Tháng 11 đến tháng 4 là mùa khô, thích hợp để ngắm cảnh và khám phá thành phố Cần Thơ.', 0, 0, 'Cầu Hậu Giang', NULL, 'https://cantho.gov.vn/cau-can-tho'),
(31, 31, 'Cầu Hoàng Văn Thụ, tại Hải Phòng, là một cây cầu mang ý nghĩa lịch sử và kiến trúc độc đáo, nối liền hai bờ sông Cấm. Đây là điểm đến lý tưởng để ngắm cảnh thành phố và tìm hiểu về lịch sử địa phương.', '00:00:00', '23:59:59', 0, 1, 'Thời gian tốt nhất để ghé thăm Cầu Hoàng Văn Thụ là vào buổi tối, khi ánh đèn chiếu sáng tạo nên khung cảnh lung linh. Tháng 3 đến tháng 10 là thời điểm lý tưởng để khám phá Hải Phòng.', 0, 0, 'Cầu Cấm', NULL, NULL),
(32, 32, 'Cầu Long Biên, biểu tượng lịch sử của Hà Nội, là cây cầu thép đầu tiên bắc qua sông Hồng, mang dấu ấn của thời kỳ thuộc địa. Với kiến trúc độc đáo và giá trị lịch sử, đây là điểm đến không thể bỏ qua khi khám phá thủ đô.', '00:00:00', '23:59:59', 0, 1, 'Thời điểm lý tưởng để ghé thăm Cầu Long Biên là vào sáng sớm hoặc chiều muộn, khi không khí mát mẻ và ánh sáng tạo nên khung cảnh đẹp mắt. Tháng 10 đến tháng 4 là thời tiết lý tưởng để tham quan.', 0, 0, 'Cầu Hà Nội', NULL, 'https://hanoi.gov.vn/cau-long-bien'),
(33, 33, 'Cầu Mỹ Thuận, cây cầu dây văng đầu tiên tại Việt Nam, nối Vĩnh Long và Tiền Giang qua sông Tiền. Với thiết kế hiện đại và tầm nhìn rộng mở, đây là điểm check-in lý tưởng cho du khách khám phá miền Tây.', '00:00:00', '23:59:59', 0, 1, 'Thời gian lý tưởng để ghé thăm Cầu Mỹ Thuận là vào buổi tối, khi ánh đèn chiếu sáng tạo nên khung cảnh lung linh. Tháng 11 đến tháng 4 là mùa khô, thích hợp để ngắm cảnh và di chuyển.', 0, 0, 'Cầu Tiền Giang', NULL, 'https://vinhlong.gov.vn/cau-my-thuan'),
(34, 34, 'Cầu Rồng, biểu tượng của Đà Nẵng, nổi bật với thiết kế hình rồng uốn lượn và khả năng phun lửa, phun nước vào cuối tuần. Đây là điểm đến không thể bỏ qua để ngắm cảnh và chụp ảnh khi đến thành phố biển.', '00:00:00', '23:59:59', 0, 1, 'Thời điểm lý tưởng để ghé thăm Cầu Rồng là vào buổi tối thứ Bảy và Chủ Nhật, khi cầu biểu diễn phun lửa và phun nước. Tháng 2 đến tháng 8 là mùa khô, thích hợp để khám phá Đà Nẵng.', 0, 0, 'Cầu Rồng Đà Nẵng', NULL, 'https://danang.gov.vn/cau-rong'),
